mapper.select:
select report_id, reporter_id, reported_id, report_content, report_seq, report_type, report_date from (select row_number()over(order by report_date desc) as rnum, report_id, reporter_id, reported_id, report_content, report_seq, report_type, report_date from report where 1=1
and reported_id like concat('%', #{search}, '%')
and report_type like concat('%', #{search}, '%')
and auth=#{search}
order by report_date desc ) a where rnum between ${start} and ${end}
