mapper.select:
select seq, ref, step, depth, id, title, content, wdate, readcount, org, filename, newfilename, category, del from (select row_number()over(order by ref desc, step asc) as rnum, seq, ref, step, depth, id, title, content, wdate, readcount, org, filename, newfilename, category, del from busybee_bbs where 1=1
and category=#{category}
and title like concat('%', #{search}, '%') and del=0
and content like concat('%', #{search}, '%') and del=0
and id=#{search} and del=0
order by ref desc, step asc) a where rnum between ${start} and ${end}

select ifnull(count(*), 0) from busybee_bbs where 1=1
and category=#{category}
and title like concat('%', #{search}, '%')
and content like concat('%', #{search}, '%')
and id=#{search}

select seq, ref, step, depth, id, title, content, wdate, readcount, org, filename, newfilename, category, del
from busybee_bbs
where seq=#{seq}

mapper.update:
update busybee_bbs
set del = 1
where seq=#{seq}

mapper.select:
select id, seq
from busybee_star
where id=#{id}
