mapper.select:
select seq, ref, step, depth, id, title, content, wdate, readcount, org, filename, newfilename, category, del, group_code from (select row_number()over(order by ref desc, step asc) as rnum, seq, ref, step, depth, id, title, content, wdate, readcount, org, filename, newfilename, category, del, group_code from busybee_bbs where 1=1 and group_code=#{group_code}
and category=#{category}
and title like concat('%', #{search}, '%') and del=0
and content like concat('%', #{search}, '%') and del=0
and id=#{search} and del=0
order by ref desc, step asc) a where rnum between ${start} and ${end}


select ifnull(count(*), 0)
from busybee_bbs
where 1=1
and group_code=#{group_code}

and category=#{category}


		and del != 1

 		and title like concat('%', #{search}, '%')


 		and content like concat('%', #{search}, '%')


 		and id=#{search}

select seq, ref, step, depth, id, title, content, wdate, readcount, org, filename, newfilename, category, del, group_code
from busybee_bbs
where seq=#{seq}

mapper.update:
update busybee_bbs
set readcount = readcount +1 
where seq=#{seq}

update busybee_bbs
set del = 1
where seq=#{seq}

mapper.select:
select id, seq
from busybee_star
where id=#{id}

mapper.delete:
delete from busybee_star
where id=#{id}
and seq=#{seq}

mapper.select:
select seq, ref, step, depth, id, title, content, wdate, readcount, org, filename, newfilename, category, del, group_code from (select row_number()over(order by ref desc, step asc) as rnum, seq, ref, step, depth, id, title, content, wdate, readcount, org, filename, newfilename, category, del, group_code from busybee_bbs where 1=1 and del != 1 and id=#{id}
and category=#{category}
and title like concat('%', #{search}, '%') and del=0
and content like concat('%', #{search}, '%') and del=0
and org=#{search}
order by ref desc, step asc) a where rnum between ${start} and ${end}


select ifnull(count(*), 0)
from busybee_bbs
where 1=1
and del != 1
and id=#{id}

and category=#{category}



 		and title like concat('%', #{search}, '%')


 		and content like concat('%', #{search}, '%')


 		and org=#{search}

select seq, ref, step, depth, id, title, content, wdate, readcount, org, filename, newfilename, category, del, group_code from (select row_number()over(order by ref desc, step asc) as rnum, bbs.seq, ref, step, depth, bbs.id, title, content, wdate, readcount, org, filename, newfilename, category, del, group_code from busybee_bbs bbs, (select * from busybee_star where id=#{id}) as sub where 1=1 and bbs.seq = sub.seq and del != 1
and category=#{category}
and title like concat('%', #{search}, '%') and del=0
and content like concat('%', #{search}, '%') and del=0
and org=#{search}
order by ref desc, step asc) a where rnum between ${start} and ${end}

select ifnull(count(*), 0) from busybee_bbs bbs, (select * from busybee_star where id=#{id}) as sub where 1=1 and bbs.seq = sub.seq and del != 1
and category=#{category}
and title like concat('%', #{search}, '%')
and content like concat('%', #{search}, '%')
and org=#{search}
