mapper.select:
select report_id, reporter_id, reported_id, report_content, report_seq, report_type, report_date, auth, report_count from (select row_number()over(order by report_count desc) as rnum, report_id, reporter_id, reported_id, report_content, report_seq, report_type, report_date, auth, report_count from report where 1=1
and reported_id like concat('%', #{search}, '%')
and report_type like concat('%', #{search}, '%')
and auth=#{search}
order by report_count desc) a where rnum between ${start} and ${end}
